mapper.insert:
insert into `org` (
    id
    , org_name
    , org_commission_money
    , create_time
    , modify_time
) values (
    #{id, javaType=long}
    , #{orgName}
    , #{orgCommissionMoney}
    , #{createTime}
    , #{modifyTime}
)

mapper.update:
update `org` set id = id
    , org_name = #{orgName}
    , org_commission_money = #{orgCommissionMoney}
    , create_time = #{createTime}
    , modify_time = #{modifyTime}
where id = #{id, javaType=long}
